"use strict";
import * as prettier from 'prettier';

function pretty(value, options = {}) {
  return prettier.format(value, {
    parser: "typescript",
    bracketSpacing: true,
    jsxSingleQuote: false,
    printWidth: 160,
    proseWrap: "always",
    semi: false,
    singleQuote: false,
    tabWidth: 2,
    trailingComma: "all",
    ...options
  });
}

export { pretty };
